document.addEventListener("DOMContentLoaded",(()=>{for(const e of document.getElementsByClassName("navbar-burger"))e.addEventListener("click",(()=>{const t=e.dataset.target,o=document.getElementById(t);e.classList.toggle("is-active"),o.classList.toggle("is-active")}))})),document.addEventListener("DOMContentLoaded",(()=>{for(const e of document.querySelectorAll(".modal-close, .modal-open, .modal-background"))e.addEventListener("click",(()=>{const t=e.dataset.target,o=document.getElementById(t),a=o.querySelector(".modal-preview");if("modal-open"===e.className){const t=e.querySelector(".modal-thumbnail").getAttribute("src");a.setAttribute("src",t)}o.classList.toggle("is-active")}))}));